import mongoose from 'mongoose'

const userSchema = new mongoose.Schema({
    name: {
        type: String,
        required: true
    },

    email: {
        type: String,
        required: true,
        unique: true
    },
    password: {
        type: String,
        required: function () {
            return !this.googleid;
        }
    },




    image: {
        type: String,
        default: 'data:image/svg+xml;base64,'

    },


    address: {
        type: Object,
        default: {
            line1: "",
            line2: ""
        }

    },

    dob: {
        type: String,
        required: true,
        default: "Not selected"
    },

    gender: {
        type: String,
        default: "Not selected"

    },
    phone: {
        type: String,
        default: "0000000000"

    },
    googleid: {
        type: Number,


    }

}, { minimize: false })



const userModel = mongoose.model('user', userSchema)

export default userModel